<style lang="scss">
    @import '../../global.scss';
    
    h1, h2, p {
        max-width: 40ch;
    }

    h1 {
        font-family: $font-main-bold;
    }
    h2, h3, p, a {
        font-family: $font-main;
    }

    nav, section, footer, .image-grid {
        margin-top: 5vh;
    }

    nav {
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
        gap: 2rem;

        a {
            text-align: center;
            flex: 1 1 10rem;
            padding: 1.5rem;
            border-radius: 10px;
            border: 4px solid $color-black;
            color: $color-black;
            text-decoration: none;
        }
        a:hover {
            border-color: $color-mint;
            color: $color-mint;
        }
     }

     section {
        text-align: center;

        h3 {
            font-size: 1.4rem;
        }
        
        div {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(max(25%, 10rem), 1fr));
        justify-items: center;
        row-gap: 4rem;
        }
     }

     footer {
        display: flex;
        flex-direction: column;
        align-items: center;
        gap: 1rem;
        
        p {
            text-align: center;
            font-size: 0.8rem;

            a {
                text-decoration: none;
                color: $color-mint;
            }
        }
     }
</style>